public class RectangleSum {
	int n, m;
	long[][] s;
	public RectangleSum(long[][] a) {
		n = a.Length;
		m = a.Length;
		s = new long[n + 1][];
		for (int i = 0; i < n + 1; i++)
			s[i] = new long[m + 1];
		for (int i = 0; i < n; i++)
			for (int j = 0; j < m; j++)
				s[i + 1][j + 1] = s[i + 1][j] + s[i][j + 1] - s[i][j] + a[i][j];
	}
	public RectangleSum(int[][] a) {
		var n = a.Length;
		var m = a.Length;
		s = new long[n + 1][];
		for (int i = 0; i < n + 1; i++)
			s[i] = new long[m + 1];
		for (int i = 0; i < n; i++)
			for (int j = 0; j < m; j++)
				s[i + 1][j + 1] = s[i + 1][j] + s[i][j + 1] - s[i][j] + a[i][j];
	}
	/// <summary>
	/// [t,b),[l,r) 
	/// </summary>
	public long Sum(int top, int left, int bottom, int right) {
		if (top < 0 || left < 0 || bottom > n || right > m || top >= bottom || left >= right) return 0;
		return s[bottom][right] - s[top][right] - s[bottom][left] + s[top][left];
	}
}

