const int Maxn = 5e5 + 9, Mod = 998244353;
int mmul(int a, int b){
    int ret;
    __asm__ __volatile__ ("\tmull %%ebx\n\tdivl %%ecx\n":"=d"(ret) : "a"(a), "b"(b), "c"(Mod));
    return ret;
}
int mplus(int a, int b) {
	a += b;
	if (a >= Mod) a -= Mod;
	return a;
}
int fpm(int base, int tims) {
	int r = 1;
	for (; tims; base = mmul(base, base), tims >>= 1) 
		if (tims & 1) r = mmul(r, base);
	return r;
}
int fac[Maxn], inv[Maxn], invFac[Maxn];

void Init() {
	fac[0] = 1;
	rep (i, 1, Maxn - 1) fac[i] = mmul(fac[i - 1], i);
	inv[1] = 1;
	rep (i, 2, Maxn - 1) inv[i] = mmul(inv[Mod % i], Mod - Mod / i);
	invFac[0] = 1;
	rep (i, 1, Maxn - 1) invFac[i] = mmul(invFac[i - 1], inv[i]);
}

int C(int n, int m) {
	if (n < m || n < 0 || m < 0) return 0;	
	return mmul(fac[n], mmul(invFac[n - m], invFac[m]));
}

int n, m;
int f(int k) {
	return mmul(C(n, k), mmul(C(m, k), mmul(fac[k], mmul(fpm(n + 1, m - k), fpm(m + 1, n - k)))));
}

void Solve() {
	n = read(), m = read();
	int ans = 0;
	rep (k, 0, min(n, m)) {
		int coef = (k & 1) ? (Mod - 1) : 1;
		ans = mplus(ans, mmul(coef, f(k)));
	}
	cout << ans << endl; 
}

