#include <bits/stdc++.h>
using namespace std;

namespace TYC
{
	typedef long long ll;
	const int N = 5e5 + 5, mod = 998244353;

	int n, m, Fac[N], Inv[N];

	inline int qpow(int x, int tim)
	{
		int ans = 1;
		for (; tim; tim >>= 1, x = int((ll)x * x % mod))
			if (tim & 1)
				ans = int((ll)ans * x % mod);
		return ans;
	}

	inline int C(const int x, const int y)
	{ return x < y ? 0 : int((ll)Fac[x] * Inv[y] % mod * Inv[x - y] % mod); }

	void work()
	{
		scanf("%d%d", &n, &m);
		if (n < m)
			swap(n, m);
		Fac[0] = 1;
		for (int i = 1; i <= n; i++)
			Fac[i] = int((ll)Fac[i - 1] * i % mod);
		Inv[n] = qpow(Fac[n], mod - 2);
		for (int i = n; i; i--)
			Inv[i - 1] = int((ll)Inv[i] * i % mod);
		int ans = 0;
		for (int r = 0, d = 1; r <= m; r++, d = -d)
			ans = int((ans + (ll)(mod + d) * C(n, r) % mod * C(m, r) % mod * Fac[r] % mod * qpow(m + 1, n - r) % mod * qpow(n + 1, m - r) % mod) % mod);
		printf("%d\n", ans);
	}
}

int main()
{
	TYC::work();
	return 0;
}

