void exgcd(int a, int b, int &x, int &y)
{
	if (b == 0)
		x = 1, y = 0;
	else
		exgcd(b, a % b, y, x), y -= x * (a / b);
}

inline int inverse(int a)
{
	int invx, invy;
	exgcd(a, MOD, invx, invy);
	return (invx % MOD + MOD) % MOD;
}

int N, M, Fac[Max_NM], Inv[Max_NM], powerM1[Max_NM], powerN1[Max_NM]; 

inline int Com(int n, int m)
{
	return Mult(Fac[n], Mult(Inv[m], Inv[n - m]));
}

int main()
{
	scanf("%d%d", &N, &M);
	Fac[0] = 1;
	for (int i = 1;i <= max(N, M);++i)
		Fac[i] = Mult(Fac[i - 1], i);
	Inv[max(N, M)] = inverse(Fac[max(N, M)]);
	for (int i = max(N, M) - 1;i >= 0;--i)
		Inv[i] = Mult(Inv[i + 1], i + 1);
	powerM1[0] = 1;
	for (int i = 1;i <= N;++i)
		powerM1[i] = Mult(powerM1[i - 1], M + 1);
	powerN1[0] = 1;
	for (int i = 1;i <= M;++i)
		powerN1[i] = Mult(powerN1[i - 1], N + 1);
	int Ans = 0;
	for (int R = 0, val;R <= min(N, M);++R)
	{
		val = Mult(Mult(Mult(Com(N, R), Com(M, R)), Fac[R]), Mult(powerM1[N - R], powerN1[M - R]));
		if (R & 1)
			Ans = Sub(Ans, val);
		else
			Ans = Add(Ans, val);
	}
	printf("%d", Ans);
	return 0;
}

