#include <cstdio>
#include <algorithm>
using namespace std;

const int Mod = 998244353;
const int Maxn = 1e6;

int QuickPow(int a, int k) {
	int ret = 1;
	while(k) {
		if(k & 1) ret = 1LL * ret * a % Mod;
		a = 1LL * a * a % Mod;
		k >>= 1;
	}
	return ret;
}
int fac[Maxn + 5], inv_fac[Maxn + 5];
void Init() {
	fac[0] = inv_fac[0] = 1;
	for(int i = 1; i <= Maxn; i++)
		fac[i] = 1LL * fac[i - 1] * i % Mod;
	inv_fac[Maxn] = QuickPow(fac[Maxn], Mod - 2);
	for(int i = Maxn - 1; i >= 1; i--)
		inv_fac[i] = 1LL * inv_fac[i + 1] * (i + 1) % Mod;
}
int C(int n, int m) {
	return 1LL * fac[n] * inv_fac[m] % Mod * inv_fac[n - m] % Mod;
}
inline void add(int &n, int m) {
	n += m;
	if(n >= Mod) n -= Mod;
}
inline void del(int &n, int m) {
	n -= m;
	if(n < 0) n += Mod;
}

int powN[Maxn + 5], powM[Maxn + 5];
int main() {
#ifdef LOACL
	freopen("in.txt", "r", stdin);
	freopen("out.txt", "w", stdout);
#endif
	int N, M;
	scanf("%d %d", &N, &M);
	Init();
	if(N > M) swap(N, M);
	powN[0] = powM[0] = 1;
	for(int i = 1; i <= M; i++)
		powN[i] = 1LL * powN[i - 1] * (N + 1) % Mod;
	for(int i = 1; i <= N; i++)
		powM[i] = 1LL * powM[i - 1] * (M + 1) % Mod;
	int ans = 0;
	for(int i = 0; i <= N; i++) {
		int tmp = 1LL * C(N, i) * C(M, i) % Mod * fac[i] % Mod * powN[M - i] % Mod * powM[N - i] % Mod;
		if(i & 1) del(ans, tmp);
		else add(ans, tmp);
	}
	printf("%d\n", ans);
	return 0;
}

