ll inv[SIZE],fac[SIZE],finv[SIZE];
void make()
{
	fac[0]=fac[1]=1;
	finv[0]=finv[1]=1;
	inv[1]=1;
	for(int i=2;i<SIZE;i++)
	{
		inv[i]=MOD-inv[MOD%i]*(MOD/i)%MOD;
		fac[i]=fac[i-1]*(ll) i%MOD;
		finv[i]=finv[i-1]*inv[i]%MOD;
	}
}
ll C(int a,int b)
{
	if(a<b) return 0;
	return fac[a]*(finv[b]*finv[a-b]%MOD)%MOD;
}
ll mpow(ll m,ll t)
{
	if(t==0) return 1;
	ll ret=mpow(m,t/2);
	ret=ret*ret%MOD;
	if(t%2==1) ret=ret*m%MOD;
	return ret;
}
int main()
{
	make();
	int n,m;
	scanf("%d %d",&n,&m);
	ll ret=0;
	for(int k=0;k<=min(n,m);k++)
	{
		ll sum=mpow(n+1,m-k)*mpow(m+1,n-k)%MOD;
		sum*=C(m,k)*C(n,k)%MOD;
		sum%=MOD;
		sum*=fac[k];
		sum%=MOD;
		if(k%2==0) ret+=sum;
		else ret-=sum;
		ret+=MOD;
		ret%=MOD;
		//printf("%lld\n",sum);
	}
	printf("%lld\n",ret);
	return 0;
}

