vector<int> psqrt(vector<int> a,int n) {
    if(n==1) return vector<int>(1,1);
    auto rem_a=a;
    rem_a.resize(n>>1);
    auto b=psqrt(rem_a,n>>1);
    auto ret=pinv(b,n);
    ret=pmul(a,ret);
    ret.resize(n);
    ret=padd(ret,b);
    ret=pmul(ret,vector<int>(1,inv2));
    return ret;
}

vector<int> pval(vector<int> a,vector<int> b) {
    int m=b.size();
    if(a.size()<b.size()) a.resize(b.size());
    if(b.size()<a.size()) b.resize(a.size());
    vector<vector<int>> prod(a.size()<<2);
    function<void(int,int,int)> getprod=[&](int x,int l,int r) {
        if(l==r) {
            prod[x].resize(2);
            prod[x][0]=sub(0,b[l]);prod[x][1]=1;
            return;
        }
        int mid=(l+r)>>1;
        getprod(x<<1,l,mid),getprod(x<<1|1,mid+1,r);
        if(x!=1) prod[x]=pmul(prod[x<<1],prod[x<<1|1]);
    };
    getprod(1,0,a.size()-1);
    vector<int> ans(a.size());
    function<void(int,int,int,vector<int>)> solve=[&](int x,int l,int r,vector<int> a) {
        if(l==r) {
            ans[l]=a[0];return;
        }
        int mid=(l+r)>>1;
        solve(x<<1,l,mid,pmod(a,prod[x<<1]));
        solve(x<<1|1,mid+1,r,pmod(a,prod[x<<1|1]));
    };
    solve(1,0,a.size()-1,a);
    ans.resize(m);
    return ans;
}

vector<int> pder(vector<int> a) {
    for(int i=1;i<a.size();i++) a[i-1]=mul(i,a[i]);
    a.pop_back();
    return a;
}

vector<int> pint(vector<int> a) {
    vector<int> inv(a.size()+1);
    inv[1]=1;for(int i=2;i<inv.size();i++) inv[i]=1ll*(mod-mod/i)*inv[mod%i]%mod;
    a.push_back(0);
    for(int i=a.size()-2;~i;i--) a[i+1]=mul(inv[i+1],a[i]);
    a[0]=0;
    return a;
}

vector<int> pln(vector<int> a,int n) {
    auto ret=pder(a);
    ret=pmul(ret,pinv(a,n));
    ret.resize(n);
    ret=pint(ret);
    ret.resize(n);
    return ret;
}

vector<int> pexp(vector<int> a,int n) {
    if(n==1) return vector<int>(1,1);
    auto rem_a=a;
    rem_a.resize(n>>1);
    auto b=pexp(rem_a,n>>1);
    auto ret=vector<int>(1,1);
    ret=psub(ret,pln(b,n));
    ret=padd(ret,a);
    ret=pmul(ret,b);
    ret.resize(n);
    return ret;
}

vector<int> pqpow(vector<int> a,int k,int n) {
	int t=a[0];
	for(auto &x:a) x=1ll*x*qpow(t,mod-2)%mod;
	vector<int> c=pln(a,n);
	for(auto &x:c) x=1ll*x*k%mod;
	c=pexp(c,n);
	for(auto &x:c) x=1ll*x*qpow(t,k)%mod;
	return c;
}

