	if(n<m) std::swap(n, m);
	fac[0]=1;
	for(int i=1; i<=n; ++i) fac[i]=(u64)fac[i-1]*i%P;
	ifac[n]=power(fac[n], P-2);
	for(int i=n; i--; ) ifac[i]=(i+1llu)*ifac[i+1]%P;
	pwn[0]=pwm[0]=1;
