	for(int r=0; r<=m; ++r) {
		u32 cur=(u64)fac[n]*ifac[n-r]%P*ifac[r]%P*fac[m]%P*ifac[m-r]%P*pwm[n-r]%P*pwn[m-r]%P;
		if(r&1) dec(ans, cur); else inc(ans, cur);
	}
