using namespace std;
typedef long long ll;
const int MAXN = 500100;
const ll MOD = 998244353;

int N, M;
ll ninv[MAXN];
ll fac[MAXN];
ll finv[MAXN];
ll npow[MAXN], mpow[MAXN];

int main()
{
    ios_base::sync_with_stdio(0);
    cin >> N >> M;

    ninv[1] = 1;
    for (int i = 2; i <= max (N, M); i++)
    {
        int K = MOD % i;
        ll r = ((ninv[K] * (K - MOD)) / i) % MOD + MOD;
        ninv[i] = r % MOD;
    }

    fac[0] = finv[0] = npow[0] = mpow[0] = 1;
    for (int i = 1; i <= max (N, M); i++)
    {
        fac[i] = (fac[i-1] * i) % MOD;
        finv[i] = (finv[i-1] * ninv[i]) % MOD;
        npow[i] = (npow[i-1] * (N + 1)) % MOD;
        mpow[i] = (mpow[i-1] * (M + 1)) % MOD;
    }

    ll ans = 0;
    for (int i = 0; i <= min (N, M); i++)
    {
        // N choose i, M choose i, i!, (M+1)^(N-i), (N+1)^(M-i)
        ll lchoose = (((fac[N] * finv[i]) % MOD) * finv[N-i]) % MOD;
        ll rchoose = (fac[M] * finv[M-i]) % MOD;
        ll nv = (lchoose * rchoose) % MOD;
        nv = (nv * mpow[N-i]) % MOD;
        nv = (nv * npow[M-i]) % MOD;
        if (i % 2 == 0)
            ans += nv;
        else
            ans -= nv;
    }

    ans = (ans % MOD + MOD) % MOD;
    cout << ans << "\n";
}

