template <typename T, typename U>
vector<T> power(const vector<T>& a, const U& b, const vector<T>& c) {
  assert(b >= 0);
  vector<U> binary;
  U bb = b;
  while (bb > 0) {
    binary.push_back(bb & 1);
    bb >>= 1;
  }
  vector<T> res = vector<T>{1} % c;
  for (int j = (int) binary.size() - 1; j >= 0; j--) {
    res = res * res % c;
    if (binary[j] == 1) {
      res = res * a % c;
    }
  }
  return res;
}

template <typename T>
vector<T> derivative(const vector<T>& a) {
  vector<T> c = a;
  for (int i = 0; i < (int) c.size(); i++) {
    c[i] *= i;   	
  }
  if (!c.empty()) {
    c.erase(c.begin());
  }
  return c;
}

template <typename T>
vector<T> primitive(const vector<T>& a) {
  vector<T> c = a;
  c.insert(c.begin(), 0);
  for (int i = 1; i < (int) c.size(); i++) {
    c[i] /= i;
  }
  return c;
}

template <typename T>
vector<T> logarithm(const vector<T>& a) {
  assert(!a.empty() && a[0] == 1);
  vector<T> u = primitive(derivative(a) * inverse(a));
  u.resize(a.size());
  return u;
}

template <typename T>
vector<T> exponent(const vector<T>& a) {
  assert(!a.empty() && a[0] == 0);
  int n = (int) a.size();
  vector<T> b = {1};
  while ((int) b.size() < n) {
    vector<T> x(a.begin(), a.begin() + min(a.size(), b.size() << 1));
    x[0] += 1;
    vector<T> old_b = b;
    b.resize(b.size() << 1);
    x -= logarithm(b);
    x *= old_b;
    for (int i = (int) b.size() >> 1; i < (int) min(x.size(), b.size()); i++) {
      b[i] = x[i];
    }
  }
  b.resize(n);
  return b;
}

template <typename T>
vector<T> multiply(const vector<vector<T>>& a) {
  if (a.empty()) {
    return {0};
  }
  function<vector<T>(int, int)> mult = [&](int l, int r) {
    if (l == r) {
      return a[l];
    }
    int y = (l + r) >> 1;
    return mult(l, y) * mult(y + 1, r);
  };
  return mult(0, (int) a.size() - 1);
}

struct Combi
{
	vector<int> fact;
	vector<int> ifact;
	vector<int> inv;
	vector<int> pow2;
	const int MOD = 998244353;
	int add(int a, int b)
	{
		a+=b;
		while(a>=MOD) a-=MOD;
		return a;
	}
	int mult(int a, int b)
	{
		return (a*1LL*b)%MOD;
	}
	int modpow(int a, int b)
	{
		int r=1;
		while(b)
		{
			if(b&1) r=mult(r,a);
			a=mult(a,a);
			b>>=1;
		}
		return r;
	}
	int choose(int a, int b)
	{
		if(a<b) return 0;
		if(b==0) return 1;
		if(a==b) return 1;
		return mult(fact[a],mult(ifact[b],ifact[a-b]));
	}
	int inverse(int a)
	{
		return modpow(a,MOD-2);
	}
	void init(int _n)
	{
		fact.clear(); ifact.clear(); inv.clear(); pow2.clear();
		fact.resize(_n+1);
		ifact.resize(_n+1);
		inv.resize(_n+1);
		pow2.resize(_n+1);
		pow2[0]=1;
		ifact[0]=1;
		fact[0]=1;
		for(int i=1;i<=_n;i++)
		{
			pow2[i]=add(pow2[i-1],pow2[i-1]);
			fact[i]=mult(fact[i-1],i);
			//ifact[i]=mult(ifact[i-1],inv[i]);
		}
		ifact[_n] = inverse(fact[_n]);
		for(int i=_n-1;i>=1;i--)
		{
		    ifact[i] = mult(ifact[i + 1], i + 1);
		}
		for(int i=1;i<=_n;i++)
		{
		    inv[i] = mult(fact[i-1],ifact[i]);
		}
	}
};
Combi combi;
const int MOD = 998244353;

int mod_pow(int a, int n, int mod) { int ret = 1; while (n) { if (n & 1) ret = ret *1LL* a % mod; a = a *1LL* a % mod; n >>= 1; } return ret; }
int inv(int n){return mod_pow(n,MOD-2,MOD);}
const int LG = 22;
const int root_pw = (1<<LG);
int iprt,prt;

void fft (vector<int> & a, bool invert) 
{
	int n = (int) a.size();

	for (int i=1, j=0; i<n; ++i) {
		int bit = n >> 1;
		for (; j>=bit; bit>>=1)
			j -= bit;
		j += bit;
		if (i < j)
			swap (a[i], a[j]);
	}

	for (int len=2; len<=n; len<<=1) {
		int wlen = invert ? iprt : prt;
		for (int i=len; i<root_pw; i<<=1)
			wlen = int((wlen*1LL*wlen)%MOD);
		for (int i=0; i<n; i+=len) {
			int w = 1;
			for (int j=0; j<len/2; ++j) {
				int u = a[i+j]; int v = int((a[i+j+len/2]*1LL*w)%MOD);
				a[i+j] = u+v < MOD ? u+v : u+v-MOD;
				a[i+j+len/2] = u-v >= 0 ? u-v : u-v+MOD;
				w = int (w * 1LL * wlen % MOD);
			}
		}
	}
	if (invert) {
		ll nrev = inv(n);
		for (int i=0; i<n; ++i)
			a[i] = int((a[i]*1LL*nrev)%MOD);
	}
}

void multiply(vector<int>& a, vector<int>& b, vector<int>& res)
{
	vector<int> fa(a.begin(), a.end()), fb(b.begin(), b.end());
	int n = 1;
	while(n < max(a.size(), b.size())) n <<= 1;
	fa.resize(n); fb.resize(n);
	fft(fa, 0); fft(fb, 0);
	res.resize(n);
	for(int i = 0; i < n; i++) 
	{
		res[i] = int((fa[i]*1LL*fb[i])%MOD);
	}
	fft(res, 1);
}

vector<int> inverse(vector<int> &a, int deg) //returns a 2^{deg} polynomial
{
	vector<int> h[2];
	h[0].pb(mod_pow(a[0],MOD-2,MOD));
	int cur=0; int pre=1;
	for(int i=0;i<deg;i++) //after this it'll be of size 2^{deg}
	{
		cur^=1; pre^=1;
		h[cur].resize((1<<(i+1)));
		h[pre].resize((1<<(i+1)));
		multiply(h[pre],h[pre],h[cur]);
		vector<int> pf;
		pf.assign((1<<(i+1)),0);
		for(int j=0;j<min(pf.size(),a.size());j++) pf[j]=a[j];
		h[cur].resize((1<<(i+2))); pf.resize((1<<(i+2)));
		multiply(pf,h[cur],h[cur]);
		h[cur].resize((1<<(i+1)));
		for(int j=0;j<(1<<i);j++)
		{
			h[cur][j] = combi.add(h[pre][j], combi.add(h[pre][j], MOD - h[cur][j]));
		}
		for(int j=(1<<i);j<(1<<(i+1));j++)
		{
			if(h[cur][j]!=0) h[cur][j] = MOD - h[cur][j];
		}
	}
	return h[cur];
}

vector<int> ln(vector<int> &a, int deg)
{
	while(a.size()<(1<<deg)) a.pb(0);
	vector<int> num,denom;
	denom = inverse(a, deg);
	num.resize((1<<deg));
	for(int i = 0; i < (1<<deg); i++)
	{
		if(i+1<a.size())
		{
			num[i] = combi.mult(a[i+1], i+1);
		}
		else
		{
			num[i] = 0;
		}
	}
	num.resize((1<<(deg+1))); denom.resize((1<<(deg+1)));
	vector<int> res;
	multiply(num,denom,res);
	res.resize(1<<deg);
	vector<int> ans(1<<deg,0);
	for(int i=1;i<(1<<deg);i++)
	{
		ans[i]=combi.mult(res[i-1],inv(i));
	}
	return ans;
}

vector<int> exp(vector<int> &a, int deg)
{	
	vector<int> h[2];
	h[0].pb(1);
	int cur=0; int pre=1;
	for(int i=0;i<deg;i++) //after this it'll be of size 2^{deg}
	{
		cur^=1; pre^=1;
		vector<int> pf;
		pf.assign((1<<(i+1)),0);
		for(int j=0;j<min(pf.size(),a.size());j++) pf[j]=a[j];
		pf[0]=combi.add(pf[0],1);
		vector<int> LN = ln(h[pre],i+1);
		for(int j=0;j<(1<<(i+1));j++)
		{
			pf[j] = combi.add(pf[j], MOD - LN[j]);
