	for(int i=0;i<=min(n,m);i++)
	{
		int res=mult(choose(n,i),mult(choose(m,i),mult(fact[i],mult(modpow(m+1,n-i),modpow(n+1,m-i)))));
		if(i&1) ans=add(ans,MOD-res);
		else ans=add(ans,res);
	}
	cout<<ans<<'\n';
}

