#define MAX_N 1000007

int N, M;
int fac[MAX_N], ifac[MAX_N], pwn[MAX_N], pwm[MAX_N];

void init() {
    int MX = 1000000;
    fac[0] = ifac[0] = pwn[0] = pwm[0] = 1;
    Rep(i, MX) {
        fac[i] = mul(fac[i - 1], i);
        pwn[i] = mul(pwn[i - 1], N + 1);
        pwm[i] = mul(pwm[i - 1], M + 1);
    }
    ifac[MX] = qpow(fac[MX], MOD - 2);
    irep(i, MX, 2) ifac[i - 1] = mul(ifac[i], i);
}

