    int res = 0, ed = std::min(N, M);
    rep(r, 0, ed) {
        int tmp = mul(C(N, r), C(M, r));
        tmp = mul(tmp, mul(fac[r], mul(pwn[M - r], pwm[N - r])));
        inc(res, (r & 1) ? MOD - tmp : tmp);
    }
    printf("%d\n", res);
}

int main() {
    read(N), read(M);
    init();
    solve();
    return 0;
}

