		int bit = n >> 1;
		for (; j>=bit; bit>>=1)
			j -= bit;
		j += bit;
		if (i < j)
			swap (a[i], a[j]);
	}

	for (int len=2; len<=n; len<<=1) {
		int wlen = invert ? iprt : prt;
		for (int i=len; i<root_pw; i<<=1)
			wlen = int((wlen*1LL*wlen)%MOD);
		for (int i=0; i<n; i+=len) {
			int w = 1;
			for (int j=0; j<len/2; ++j) {
				int u = a[i+j]; int v = int((a[i+j+len/2]*1LL*w)%MOD);
				a[i+j] = u+v < MOD ? u+v : u+v-MOD;
				a[i+j+len/2] = u-v >= 0 ? u-v : u-v+MOD;
				w = int (w * 1LL * wlen % MOD);
			}
		}
	}
	if (invert) {
		ll nrev = inv(n);
		for (int i=0; i<n; ++i)
			a[i] = int((a[i]*1LL*nrev)%MOD);
	}
}

void multiply(vector<int>& a, vector<int>& b, vector<int>& res)
{
	vector<int> fa(a.begin(), a.end()), fb(b.begin(), b.end());
	int n = 1;
	while(n < max(a.size(), b.size())) n <<= 1;
	fa.resize(n); fb.resize(n);
	fft(fa, 0); fft(fb, 0);
	res.resize(n);
	for(int i = 0; i < n; i++) 
	{
		res[i] = int((fa[i]*1LL*fb[i])%MOD);
	}
	fft(res, 1);
}

vector<int> inverse(vector<int> &a, int deg) //returns a 2^{deg} polynomial
{
	vector<int> h[2];
	h[0].pb(mod_pow(a[0],MOD-2,MOD));
	int cur=0; int pre=1;
	for(int i=0;i<deg;i++) //after this it'll be of size 2^{deg}
	{
		cur^=1; pre^=1;
		h[cur].resize((1<<(i+1)));
		h[pre].resize((1<<(i+1)));
		multiply(h[pre],h[pre],h[cur]);
		vector<int> pf;
		pf.assign((1<<(i+1)),0);
		for(int j=0;j<min(pf.size(),a.size());j++) pf[j]=a[j];
		h[cur].resize((1<<(i+2))); pf.resize((1<<(i+2)));
		multiply(pf,h[cur],h[cur]);
		h[cur].resize((1<<(i+1)));
		for(int j=0;j<(1<<i);j++)
		{
			h[cur][j] = combi.add(h[pre][j], combi.add(h[pre][j], MOD - h[cur][j]));
		}
		for(int j=(1<<i);j<(1<<(i+1));j++)
		{
			if(h[cur][j]!=0) h[cur][j] = MOD - h[cur][j];
		}
	}
	return h[cur];
}

vector<int> ln(vector<int> &a, int deg)
{
	while(a.size()<(1<<deg)) a.pb(0);
	vector<int> num,denom;
	denom = inverse(a, deg);
	num.resize((1<<deg));
	for(int i = 0; i < (1<<deg); i++)
	{
		if(i+1<a.size())
		{
			num[i] = combi.mult(a[i+1], i+1);
		}
		else
		{
			num[i] = 0;
		}
	}
	num.resize((1<<(deg+1))); denom.resize((1<<(deg+1)));
	vector<int> res;
	multiply(num,denom,res);
	res.resize(1<<deg);
	vector<int> ans(1<<deg,0);
	for(int i=1;i<(1<<deg);i++)
	{
		ans[i]=combi.mult(res[i-1],inv(i));
	}
	return ans;
}

vector<int> exp(vector<int> &a, int deg)
{	
	vector<int> h[2];
	h[0].pb(1);
	int cur=0; int pre=1;
	for(int i=0;i<deg;i++) //after this it'll be of size 2^{deg}
	{
		cur^=1; pre^=1;
		vector<int> pf;
		pf.assign((1<<(i+1)),0);
		for(int j=0;j<min(pf.size(),a.size());j++) pf[j]=a[j];
		pf[0]=combi.add(pf[0],1);
		vector<int> LN = ln(h[pre],i+1);
		for(int j=0;j<(1<<(i+1));j++)
		{
			pf[j] = combi.add(pf[j], MOD - LN[j]);
		}
		h[pre].resize((1<<(i+2))); pf.resize((1<<(i+2)));
		multiply(pf,h[pre],h[cur]);
		h[cur].resize((1<<(i+1)));
	}
	return h[cur];
}

