static const int64 mod = 998244353LL;
int n,m;
int64 pown1[500003],powm1[500003];
int64 fac[500003],inv[500003],invfac[500003];
int main()
{
	scanf("%d%d",&n,&m);
	pown1[0] = 1;
	for(int i = 1;i <= m;++i) {
		pown1[i] = pown1[i-1] * (n+1) % mod;
	}
	powm1[0] = 1;
	for(int i = 1;i <= n;++i) {
		powm1[i] = powm1[i-1] * (m+1) % mod;
	}
	inv[1] = fac[0] = fac[1] = invfac[0] = invfac[1] = 1;
	for(int i = 2;i <= 500000;++i) {
		fac[i] = (fac[i-1] * i) % mod;
		inv[i] = (mod-mod/i) * inv[mod%i] % mod;
		invfac[i] = (invfac[i-1]*inv[i]) % mod;
	}
	int64 ans = 0;
	for(int i = 0;i <= n && i <= m;++i) {
		ans += pown1[m-i] * powm1[n-i] % mod * fac[n] % mod * invfac[n-i] % mod * fac[m] % mod * invfac[m-i] % mod * invfac[i] % mod * ((i&1)==0?1:-1);
	}
	ans %= mod;
	if(ans < 0) ans += mod;
	printf("%lld\n", ans);
}

