const int maxn=500000;
int read()
{
	char c=getchar();
	int res=0;
	while(c<'0'||c>'9') c=getchar();
	while(c>='0'&&c<='9') res=(res<<1)+(res<<3)+(c^48),c=getchar();
	return res;
}
long long ksm(long long x,long long n)
{
	long long ans=1;
	while(n)
	{
		if(n&1) ans=ans*x%mod;
		x=x*x%mod;
		n>>=1;
	}
	return ans;
}
int n,m;
long long fir[maxn+5],inv[maxn+5];
long long C(int n,int m)
{
	if(n<m) return 0;
	return fir[n]*inv[m]%mod*inv[n-m]%mod;
}
void pre()
{
	fir[0]=1;
	for(int i=1;i<=maxn;i++) fir[i]=fir[i-1]*i%mod;
	inv[maxn]=ksm(fir[maxn],mod-2);
	for(int i=maxn-1;i>=0;i--) inv[i]=inv[i+1]*(i+1)%mod;
}
int main()
{
	pre();
	n=read(),m=read();
	long long ans=0;
	for(int i=0;i<=min(n,m);i++)
	{
		if(i&1) ans=(ans-C(n,i)*C(m,i)%mod*ksm(m+1,n-i)%mod*ksm(n+1,m-i)%mod*fir[i]%mod)%mod;
		else ans=(ans+C(n,i)*C(m,i)%mod*ksm(m+1,n-i)%mod*ksm(n+1,m-i)%mod*fir[i]%mod)%mod;
	}
	printf("%lld\n",ans);
}

