int fac[MAXN], ifac[MAXN], inv[MAXN];
int muln[MAXN], mulm[MAXN];

inline void Add(int &x, int y) { x += y; if(x >= MOD) x -= MOD; if(x < 0) x += MOD; }

inline int C(int n, int m) {
	if(n < m) return 0;
	return (LL) fac[n] * ifac[m] % MOD * ifac[n - m] % MOD;
}

int n, m, ans;

inline int f(int x) { return (LL) C(n, x) * C(m, x) % MOD * fac[x] % MOD * muln[m - x] % MOD * mulm[n - x] % MOD; }

inline void init() {
	fac[0] = ifac[0] = inv[1] = 1;
	for(int i = 1; i < MAXN; ++i) fac[i] = (LL) fac[i - 1] * i % MOD;
	for(int i = 2; i < MAXN; ++i) inv[i] = (LL) (MOD / i) * (MOD - inv[MOD % i]) % MOD;
	for(int i = 1; i < MAXN; ++i) ifac[i] = (LL) ifac[i - 1] * inv[i] % MOD;

	n = read<int>(); m = read<int>();
	muln[0] = mulm[0] = 1;
	muln[1] = n + 1; mulm[1] = m + 1;
	for(int i = 2; i < MAXN; ++i) muln[i] = (LL) muln[i - 1] * muln[1] % MOD;
	for(int i = 2; i < MAXN; ++i) mulm[i] = (LL) mulm[i - 1] * mulm[1] % MOD;
}

inline void solve() {
	int tp = min(n, m);
	for(int i = 0; i <= tp; ++i) {
		Add(ans, f(i) * sig[i & 1]);
	}
	printf("%d\n", ans);
}

int main() {

	init();
	solve();

	return 0;
}

