const int mod=998244353;
inline int add(int a,int b){a+=b;return a>=mod?a-mod:a;}
inline int sub(int a,int b){a-=b;return a<0?a+mod:a;}
inline int mul(int a,int b){return (ll)a*b%mod;}
inline int qpow(int a,int b){int ret=1;for(;b;b>>=1,a=mul(a,a))if(b&1)ret=mul(ret,a);return ret;}
/* math */
const int N = 5e5+5;
int fac[N],ifac[N];
int pwn[N], pwm[N];
int nn,mm;
inline void init(int n=5e5){
	fac[0]=ifac[0]=1;for(int i=1;i<=n;i++)fac[i]=mul(fac[i-1],i);
	ifac[n]=qpow(fac[n],mod-2);for(int i=n-1;i;i--)ifac[i]=mul(ifac[i+1],i+1);
	pwn[0]=pwm[0]=1;
	for(int i=1;i<=mm;i++)pwn[i]=mul(pwn[i-1],nn+1);
	for(int i=1;i<=nn;i++)pwm[i]=mul(pwm[i-1],mm+1);
}
inline int binom(int a,int b){
	if(b>a)return 0;
	return mul(fac[a],mul(ifac[a-b],ifac[b]));
}

int main()
{
	cin >> nn >> mm;
	init();
	int ans=0;
	for(int d=0,s=1;d<=min(nn,mm);++d,s=mod-s){
		// cout << d << ":" << mm-d << ":" << pwn[mm-d] << ":" << mul(pwn[mm-d],pwm[nn-d]) << endl;
		int sum = mul(mul(mul(binom(nn,d),binom(mm,d)),fac[d]),mul(pwn[mm-d],pwm[nn-d]));
		ans=add(ans, mul(s,sum));
	}
	cout << ans << endl;
}

