using cat = long long;

cat mod = 998244353;

cat pw(cat a, cat e) {
	if(e <= 0) return 1;
	cat x = pw(a, e/2);
	x = x * x % mod;
	if(e&1) x = x * a % mod;
	return x;
}

cat om[22];

void ntt(cat * a, int n, bool rev = 0) {
	for(int i = 0, j = 0; i < n; ++i) {
		if(i < j) swap(a[i], a[j]);
		for(int k = n>>1; (j^=k) < k; k >>= 1) {}
	}
	for(int i = 2, e = 1; i <= n; i <<= 1, e++) {
		int len = i>>1;
		cat w = om[e];
		for(int j = 0; j < n; j += i) {
			cat cur = 1;
			for(int k = j; k < j+len; ++k) {
				cat u = a[k], v = a[k+len] * cur % mod;
				a[k] = u + v, a[k+len] = u - v;
				if(a[k] >= mod) a[k] -= mod;
				if(a[k+len] < 0) a[k+len] += mod;
				cur = cur * w % mod;
			}
		}
	}
	if(rev) {
		cat n_inv = pw(n, mod-2);
		reverse(a+1, a+n);
		for(int i = 0; i < n; i++) a[i] = a[i] * n_inv % mod;
	}
}

