int qpow(int a, int b)
{
	int res = 1;
	while(b)
	{
		if(b%2)
			res = res*a %P;

		a = a*a %P;
		b /= 2; 
	}
	return res;
}

int getInv(int a)
{	
	return qpow(a,P-2);
}

void init()
{
	f[0] = 1;

	int t = max(n,m);

	for(int i = 1; i <= t; i++)
	{
		f[i] = (f[i-1]*i)%P;
	}
	finv[t] = getInv(f[t]) % P;

	for(int i = t; i > 0; i--)
	{
		finv[i-1] = finv[i]*i %P;
	}

	nk[0] = 1, mk[0] = 1;
	for(int i = 1; i <= t; i++)
	{
		nk[i] = nk[i-1]*(n+1) % P;
		mk[i] = mk[i-1]*(m+1) % P;
	}
}

int choose(int a, int b)
{
	int A = f[a]*finv[b] % P;
	int B = A*finv[a-b] % P;

	int res = B;

	return res; 
}

