int fac[maxn], ifac[maxn];
inline void init(int N) {
	fac[0] = 1;
	for (int i = 1; i <= N; i++) fac[i] = (LL) fac[i - 1] * i % mod;
	ifac[N] = Pow(fac[N], mod - 2);
	for (int i = N - 1; ~i; i--) ifac[i] = (LL) ifac[i + 1] * (i + 1) % mod;
}
inline int C(int _n, int _m) { return (LL) fac[_n] * ifac[_n - _m] % mod * ifac[_m] % mod ; }

inline int f(int k) { return (LL) C(n, k) * C(m, k) % mod * fac[k] % mod * Pow(m + 1, n - k) % mod * Pow(n + 1, m - k) % mod; }

