namespace ModCalculator {

inline void Inc(int &x, int y) {
	x += y; if (x >= MOD) x -= MOD;
}
inline void Dec(int &x, int y) {
	x -= y; if (x < 0) x += MOD;
}
inline int Add(int x, int y) {
	Inc(x, y); return x;
}
inline int Sub(int x, int y) {
	Dec(x, y); return x;
}
inline int Mul(int x, int y) {
	return 1LL * x * y % MOD;
}

