cat mod = 998244353;

cat pw(cat a, cat e) {
	if(e <= 0) return 1;
	cat x = pw(a, e/2);
	x = x * x % mod;
	if(e&1) x = x * a % mod;
	return x;
}

cat om[22];

void ntt(cat * a, int n, bool rev = 0) {
	for(int i = 0, j = 0; i < n; ++i) {
		if(i < j) swap(a[i], a[j]);
		for(int k = n>>1; (j^=k) < k; k >>= 1) {}
	}
	for(int i = 2, e = 1; i <= n; i <<= 1, e++) {
		int len = i>>1;
		cat w = om[e];
		static cat om_pw[1<<19];
		om_pw[0] = 1;
		for(int j = 1; j < len; j++) om_pw[j] = om_pw[j-1] * w % mod;
		for(int j = 0; j < n; j += i) {
			cat * cur_ptr = om_pw;
			cat * l_ptr = a + j;
			cat * r_ptr = a + j + len;
			for(int k = 0; k < len; k++) {
				cat u = *l_ptr, v = (*r_ptr) * (*(cur_ptr++)) % mod;
				cat s = u + v, d = u - v;
				if(s >= mod) s -= mod;
				if(d < 0) d += mod;
				*(l_ptr++) = s;
				*(r_ptr++) = d;
			}
		}
	}
	if(rev) {
		cat n_inv = pw(n, mod-2);
		reverse(a+1, a+n);
		for(int i = 0; i < n; i++) a[i] = a[i] * n_inv % mod;
	}
}

