				const int s=(1<<w)-1;
				const Mint g=Mint(prim_root).pow((mod-1)>>w),ig=g.inv();
				Mint p=1,ip=1;
				rep(i,1<<w){
					roots[s+i]=p;p*=g;
					iroots[s+i]=ip;ip*=ig;
				}
			}
		}
