}

const int LOG = 20;
const int N = 1 << LOG;
const int NN = N + 5;
ll w[NN];
int binRev[NN];
ll RN;

void initFFT() {
	ll W = 2;
	for(;; W++) {
		ll x = W;
		for (int i = 1; i < LOG; i++)
			x = mult(x, x);
		if (x == MOD - 1) break;
	}
	w[0] = 1;
	for (int i = 1; i < N; i++)
		w[i] = mult(w[i - 1], W);
	for (int mask = 1; mask < N; mask++)
		binRev[mask] = (binRev[mask >> 1] >> 1) ^ ((mask & 1) << (LOG - 1));
	RN = rev(N);
}
void FFT(ll* A) {
	for (int mask = 0; mask < N; mask++)
		if (mask < binRev[mask])
			swap(A[mask], A[binRev[mask]]);
	for (int lvl = 0; lvl < LOG; lvl++) {
		int len = 1 << lvl;
		for (int st = 0; st < N; st += len * 2)
			for (int i = 0; i < len; i++) {
				ll x = A[st + i], y = mult(A[st + len + i], w[i << (LOG - 1 - lvl)]);
				A[st + i] = add(x, y);
				A[st + len + i] = sub(x, y);
			}
	}
}

