    const int G = 3;
    inline int add(int x, int y) { x += y; return x >= mo ? x - mo : x; }
    inline int sub(int x, int y) { x -= y; return x < 0 ? x + mo : x; }
    inline int mul(int x, int y) { return (lo) x * y % mo; }
    inline int power(int a, lo k = mo - 2) {
		int ans = 1;
		for (; k; k >>= 1, a = mul(a, a))
			if (k & 1) ans = mul(ans, a);
		return ans;
    }
    inline int getind(int x) {
		unordered_map <int, int> mp;
		int K = sqrt(mo), ix = power(x);
		for (int t = 1, k = 0; k <= K; k++, t = mul(t, G))
			mp[t] = k;
		int p = power(G, K);
		for (int t = p, k = 1; k <= K; k++, t = mul(t, p))
			if (mp.count(mul(t, ix))) {
				return k * K - mp[mul(t, ix)];
			}
		assert(0);
    }
    inline int getSqrt(int x) {
		int t = power(G, getind(x) / 2);
		return min(t, mo - t);
    }
}
