const int N = (1 << 20) + 233;
namespace FFT {
    int fft_n, fft_l, rev[N];
    ul *data[30], wn[N << 1];

    inline void prepare(int n) {
		fft_n = 1; fft_l = -1;
		while (fft_n < n) fft_n <<= 1, ++fft_l;
		re0 (i, fft_n) rev[i] = (rev[i >> 1] >> 1) | ((i & 1) << fft_l);
	ul *cur = wn;
	for (int cn = 2, step = 0; cn <= fft_n; cn <<= 1, ++step) {
	    data[step] = cur;
	    ul t = power(G, (mo - 1) / cn), w = 1;
	    re0 (k, cn) *cur++ = w, w = w * t % mo;
	}
    }
    inline void dft(ul *a, int n) {
	re0 (i, n) if (i < rev[i]) swap(a[i], a[rev[i]]);
	for (int l = 2, step = 0; l <= fft_n; l <<= 1, ++step) 
	    for (int i = 0, m = l >> 1; i < fft_n; i += l) {
		ul *l = a + i, *r = a + i + m, *w = data[step];
		for (int k = 0; k < m; k++, ++l, ++r, ++w) {
		    ul y = *r * *w % mo;
		    *r = *l + mo - y; *l += y;
		}
	    }
	re0 (i, n) a[i] %= mo;
    }
    inline void idft(ul *a, int n) {
	dft(a, n);
	reverse(a + 1, a + n);
	int invn = power(n);
	re0 (i, n) a[i] = mul(a[i], invn);
    }
    poly realmain(poly a, poly b, int op = 1) {
	static ul x[N], y[N];
	int n = a.size(), m = b.size();
	prepare(n * op + m - 1);
	re0 (i, fft_n) {
	    x[i] = i < n ? a[i] : 0;
	    y[i] = i < m ? b[i] : 0;
	}
	dft(x, fft_n); dft(y, fft_n);
	re0 (i, fft_n) x[i] = mul(op == 2 ? x[i] : 1, mul(x[i], y[i]));
	idft(x, fft_n);
	poly ans(n + m - 1, 0);
	re0 (i, ans.size()) ans[i] = x[i];
	return ans;
    }
}

poly K(poly a, int l) { a.resize(l); return a; }
poly rev(poly a) { reverse(a.begin(), a.end()); return a; }

inline void ovo(poly a) {
