poly operator + (poly a, poly b) {
    if (a.size() < b.size()) a.resize(b.size());
    re0 (i, b.size()) a[i] = add(a[i], b[i]);
    return a;
}

poly operator - (poly a, poly b) {
    if (a.size() < b.size()) a.resize(b.size());
    re0 (i, b.size()) a[i] = sub(a[i], b[i]);
    return a;
}

poly operator * (poly a, poly b) {
    if (a.size() + b.size() < 128) {
	if (!a.size() && !b.size()) return a;
	poly ans(a.size() + b.size() - 1, 0);
	re0 (i, a.size()) re0 (j, b.size()) ans[i + j] = add(ans[i + j], mul(a[i], b[j]));
	return ans;
    }
    return FFT::realmain(a, b);
}

poly operator * (poly a, int k) {
    re0 (i, a.size()) a[i] = mul(a[i], k);
    return a;
}

poly Der(poly a) {
    if (!a.size()) return poly(1, 0);
    poly t(a.size() - 1, 0);
    rep (i, t.size()) t[i - 1] = mul(a[i], i);
    return t;
}

poly Int(poly a) {
    static int inv[N];
    inv[0] = inv[1] = 1;
    for (int i = 2; i <= (int) a.size(); i++)
	inv[i] = mul(mo - mo / i, inv[mo % i]);
    poly t(a.size() + 1, 0);
    rep (i, a.size()) t[i] = mul(a[i - 1], inv[i]);
    return t;
}

poly Inv(poly a, int n) {
    a.resize(n);
    if (n == 1) return poly(1, power(a[0]));
    poly b = Inv(a, (n + 1) >> 1); 
    return K(b * 2 - K(FFT::realmain(b, a, 2), n), n);
}

poly Ln(poly a, int n) {
    return Int(K(Der(a) * Inv(a, n), n - 1));
}

poly Exp(poly a, int n) {
    a.resize(n);
    if (n == 1) return assert(a[0] == 0), poly(1, 1);
    poly b = Exp(a, (n + 1) >> 1);
    return K(b * (a + poly(1, 1) - Ln(b, n)), n);
}

poly Sqrt(poly a, int n) {
    a.resize(n);
    if (n == 1) return poly(1, getSqrt(a[0]));
    poly b = Sqrt(a, (n + 1) >> 1);
    return (b + K(a * Inv(b, n), n)) * power(2);
}

poly power(poly a, lo k) {
    int n = a.size();
    int p = 0;
    while (p < a.size() && !a[p]) ++p;
    if (p == a.size() || p * k > n) return poly(n, 0);
    poly b(a.size() - p, 0);
    int tmp = a[p], t = power(a[p]);
    re0 (i, b.size()) b[i] = mul(t, a[i + p]);
    b = Exp(Ln(b, n - p) * k, n - p);
    b = rev(b); b.resize(b.size() + p * k); b = rev(b);
    return K(b * power(tmp, k), n);
}

