const int MAXDIM=500000;
const int MOD=998244353;
void inc(int &a,int b) { if((a+=b)>=MOD) a-=MOD; }
void dec(int &a,int b) { inc(a,MOD-b); }




int h,w;


int inv[MAXDIM+1];
int fac[MAXDIM+1];
int ifac[MAXDIM+1];

int freerowways[MAXDIM+1];
int freecolways[MAXDIM+1];

int choose(int n,int k) { assert(0<=n&&n<=max(h,w)); if(k<0||k>n) return 0; return (ll)fac[n]*ifac[k]%MOD*ifac[n-k]%MOD; }




int solve() {
	inv[1]=1; FORE(i,2,MAXDIM) inv[i]=(ll)(MOD-MOD/i)*inv[MOD%i]%MOD;
	fac[0]=1; FORE(i,1,MAXDIM) fac[i]=(ll)fac[i-1]*i%MOD;
	ifac[0]=1; FORE(i,1,MAXDIM) ifac[i]=(ll)ifac[i-1]*inv[i]%MOD;

	freerowways[0]=1; FORE(i,1,h) freerowways[i]=(ll)freerowways[i-1]*(w+1)%MOD;
	freecolways[0]=1; FORE(i,1,w) freecolways[i]=(ll)freecolways[i-1]*(h+1)%MOD;

	int ret=0;
	REPE(nfixed,min(h,w)) {
		int ways=(ll)choose(w,nfixed)*choose(h,nfixed)%MOD*freerowways[h-nfixed]%MOD*freecolways[w-nfixed]%MOD*fac[nfixed]%MOD;
		if(nfixed%2==0) inc(ret,ways); else dec(ret,ways);
	}
	return ret;
}

void run() {
	scanf("%d%d",&h,&w);
	printf("%d\n",solve());
}

int main() {
	run();
	return 0;
}

