int qpow(int a, int b){
    int ret = 1;
    while(b){
        if(b & 1) ret = (ll)ret * a % mod;
        a = (ll)a * a % mod; b >>= 1;
    }
    assert(ret != 0);
    return ret;
}
void init(){
    fac[0] = 1;
    for(int i = 1; i <= N; i++) fac[i] = (ll)fac[i - 1] * i % mod;
    inv[N] = qpow(fac[N], mod - 2);
    for(int i = N - 1; i; i--) inv[i] = (ll)inv[i + 1] * (i + 1) % mod;
}
void upd(int &x, int add){
    x += add;
    if(x >= mod) x -= mod;
}
int C(int a, int b){
    if(a == b || b == 0) return 1;
    return (ll)fac[a] * inv[b] % mod * inv[a - b] % mod;
}

int main(){
    scanf("%d %d", &n, &m); init();
    for(int i = 0, k = 1; i <= min(n, m); i++, k = -k){
        upd(ans, ((ll)k * C(n, i) % mod * C(m, i) % mod * fac[i] % mod * qpow(m + 1, n - i) % mod * qpow(n + 1, m - i) % mod + mod) % mod);
    }
