int fac[maxN + 1], inv[maxN + 1];

inline int ADD(int x, int y) { return x + y >= mod ? x + y - mod : x + y; }

inline int SUB(int x, int y) { return x - y < 0 ? x - y + mod : x - y; }

inline int mpow(int a, int x)
{
	int ans = 1;
	while(x)
	{
		if(x & 1) ans = 1ll * ans * a % mod;
		a = 1ll * a * a % mod;
		x >>= 1;
	}
	return ans;
}

inline int C(int n, int m)
{
	if(n < m) return 0;
	return 1ll * fac[n] * inv[m] % mod * inv[n - m] % mod;
}

int main()
{
	scanf("%d %d", &n, &m);
	int lim = max(n, m), ans = 0;

	fac[0] = 1;
	for(int i = 1; i <= lim; i++) fac[i] = 1ll * fac[i - 1] * i % mod;
	inv[lim] = mpow(fac[lim], mod - 2);
	for(int i = lim - 1; i >= 0; i--) inv[i] = 1ll * inv[i + 1] * (i + 1) % mod;

	lim = min(n, m);
	for(int i = 0; i <= lim; i++)
	{
		int res = 1ll * C(n, i) * C(m, i) % mod * fac[i] % mod * mpow(n + 1, m - i) % mod * mpow(m + 1, n - i) % mod;
		ans = (i & 1) ? SUB(ans, res) : ADD(ans, res);
	}

	printf("%d", ans);
	return 0;
}

