		ans=(ans+(i&1?-1ll:1ll)*c(n,i)*c(m,i)%mod*fac[i]%mod*power(n+1,m-i)%mod*power(m+1,n-i))%mod;
	}
	printf("%d\n",(ans+mod)%mod);
}

