#include<bits/stdc++.h>
#define fo(i, x, y) for(int i = x, _b = y; i <= _b; i ++)
#define ff(i, x, y) for(int i = x, _b = y; i <  _b; i ++)
#define fd(i, x, y) for(int i = x, _b = y; i >= _b; i --)
#define ll long long
#define pp printf
#define hh pp("\n")
#define dub(...) fprintf(stderr, _VA_ARGS)
using namespace std;

const int mo = 998244353;

ll ksm(ll x, ll y) {
	ll s = 1;
	for(; y; y /= 2, x = x * x % mo)
		if(y & 1) s = s * x % mo;
	return s;
}

const int N = 5e5 + 5;

int n, m;
ll fac[N], nf[N];

void build(int n) {
	fac[0] = 1; fo(i, 1, n) fac[i] = fac[i - 1] * i % mo;
	nf[n] = ksm(fac[n], mo - 2); fd(i, n, 1) nf[i - 1] = nf[i] * i % mo;
}

ll C(int n, int m) {
	if(n < m) return 0;
	return fac[n] * nf[m] % mo * nf[n - m] % mo;
}

int main() {
	build(5e5);
	scanf("%d %d", &n, &m);
	ll ans = 0;
	fo(k, 0, min(n, m))
		ans += (k & 1 ? -1 : 1) * C(n, k) * C(m, k) % mo * fac[k] % mo * ksm(m + 1, n - k) % mo * ksm(n + 1, m - k) % mo;
	ans = (ans % mo + mo) % mo;
	pp("%lld\n", ans);
}

