int f[N];
int finv[N];

int nk[N];
int mk[N];

int n,m;

void ex_gcd(int a, int b, int &x, int &y)
{
	if(!b)
	{
		x = 1;
		y = 0;
		return;
	}
	ex_gcd(b,a%b,y,x);
	y -= a/b*x;
}

int getInv(int a)
{
	int x,y;
	ex_gcd(a,P,x,y);

	x = (x%P+P)%P;

	return x;
}

void init()
{
	f[0] = 1;

	int t = max(n,m);

	for(int i = 1; i <= t; i++)
	{
		f[i] = (f[i-1]*i)%P;
	}
	finv[t] = getInv(f[t]) % P;

	for(int i = t; i > 0; i--)
	{
		finv[i-1] = finv[i]*i %P;
	}

	nk[0] = 1, mk[0] = 1;
	for(int i = 1; i <= t; i++)
	{
		nk[i] = nk[i-1]*(n+1) % P;
		mk[i] = mk[i-1]*(m+1) % P;
	}
}

int choose(int a, int b)
{
	int A = f[a]*finv[b] % P;
	int B = A*finv[a-b] % P;

	int res = B;

	return res; 
}

long long ans;

int cal(int k)
{
	int res = (( (nk[m-k]*mk[n-k])%P * f[k])%P *(choose(n,k)*choose(m,k))%P) % P;

	int A = nk[m-k]*mk[n-k]%P;
	int B = A*f[k] %P;
	int C = B*choose(n,k) %P;
	int D = C*choose(m,k) %P;
	res = D;

	if(k%2)
	{
		return -res;
	}
	else
	{
		return res;
	}
}

