using namespace std;

const int P = 998244353;

const int N = 5e5+5;

int f[N];
int finv[N];

int nk[N];
int mk[N];

int n,m;

void ex_gcd(int a, int b, int &x, int &y)
{
	if(!b)
	{
		x = 1;
		y = 0;
		return;
	}
	ex_gcd(b,a%b,y,x);
	y -= a/b*x;
}

int getInv(int a)
{
	int x,y;
	ex_gcd(a,P,x,y);

	x = (x%P+P)%P;

	return x;
}

void init()
{
	f[0] = 1;

	int t = max(n,m);

	for(int i = 1; i <= t; i++)
	{
		f[i] = (f[i-1]*i)%P;
		if(f[i] < 0) puts("WTF1");
	}
	finv[t] = getInv(f[t]) % P;
	if(finv[t] < 0)
	{
