	}
	for(int i = t; i > 0; i--)
	{
		finv[i-1] = finv[i]*i %P;
		if(finv[i-1] < 0) puts("WTF2");
	}

	nk[0] = 1, mk[0] = 1;
	for(int i = 1; i <= t; i++)
	{
		nk[i] = nk[i-1]*(n+1) % P;
		mk[i] = mk[i-1]*(m+1) % P;
