int cal(int k)
{
	int res = (( (nk[m-k]*mk[n-k])%P * f[k])%P *(choose(n,k)*choose(m,k))%P) % P;

	int A = nk[m-k]*mk[n-k]%P;
	int B = A*f[k] %P;
	int C = B*choose(n,k) %P;
	int D = C*choose(m,k) %P;
	res = D;

	if(k%2)
	{
		return -res;
	}
	else
	{
		return res;
	}
}

