int qpow(int base,int tms)
{
    int tmp = 1;
    while(tms)
    {
        if(tms & 1) tmp = tmp * 1ll * base % mod;
        base = base * 1ll * base % mod;
        tms >>= 1;
    }
    return tmp;
}

int C(int n,int r)
{
    return fact[n] * 1ll * inv[r] % mod * inv[n - r] % mod;
}

int main()
