int solve() {
	inv[1]=1; FORE(i,2,MAXDIM) inv[i]=(ll)(MOD-MOD/i)*inv[MOD%i]%MOD;
	fac[0]=1; FORE(i,1,MAXDIM) fac[i]=(ll)fac[i-1]*i%MOD;
	ifac[0]=1; FORE(i,1,MAXDIM) ifac[i]=(ll)ifac[i-1]*inv[i]%MOD;

	vector<int> a(h+1);
	REPE(i,h) a[i]=(ll)(h+1-i)*ifac[i]%MOD;
	//printf("a:"); REPSZ(i,a) printf(" %d",a[i]); puts("");

	vector<int> b=pw(a,w);
	//printf("b:"); REPSZ(i,b) printf(" %d",b[i]); puts("");

	int ret=0;
	REPE(i,h) ret=(ret+(ll)b[i]*fac[h]%MOD*ifac[h-i])%MOD;
	return ret;
}

void run() {
	scanf("%d%d",&h,&w);
	printf("%d\n",solve());
}

int main() {
	run();
	return 0;
}

