	for (int i = 0; i <= n && i <= m; ++i) {
		int w = 1ll * comb(n, i) % mod * comb(m, i) % mod * fac[i] % mod;
		w = 1ll * w * pm[n - i] % mod * pn[m - i] % mod;
		if (i & 1) ans = (ans - w + mod) % mod;
		else (ans += w) %= mod;
	}
