	fac[0] = faci[0] = inv[0] = inv[1] = pwn[0] = pwm[0] = 1;
	for(int i=1; i<=x; i++)
	{
		fac[i] = fac[i-1] * i % MOD;
		if(i > 1) inv[i] = (MOD*MOD-(MOD/i)*inv[MOD%i]) % MOD;
		faci[i] = faci[i-1] * inv[i] % MOD;
		pwn[i] = pwn[i-1] * (n+1) % MOD;
		pwm[i] = pwm[i-1] * (m+1) % MOD;
	}
}

