	for(int i=0; i<=min(n, m); i++)
	{
		ll coe = (i&1) ? (MOD-1) : (+1);
		ans = (ans + coe*C(n, i)%MOD*C(m, i)%MOD*pwn[m-i]%MOD*pwm[n-i]%MOD*fac[i]) % MOD;
	}
