def p(i):
  ans = 1
  while i != 1:
    ans *= i
    i -= 1
  return ans  
def comb_dp_sub(n, r):
    global comb_table
    if r == 0 or n == r:
        return 1
    else:
        return comb_table[n - 1][r] + comb_table[n - 1][r - 1]

def comb_dp(n, r):
    global comb_table
    comb_table = [[0] * (n + 1) for _ in range(n + 1)]
    for i in range(n + 1):
        for j in range(i + 1):
            comb_table[i][j] = comb_dp_sub(i, j)
    return comb_table[n][r]

