inline void fwt(int *a,int n,int tag){
	for(int i=1;i<n;i<<=1){
		for(int j=0,I=i<<1;j<n;j+=I){
			for(int k=0;k<i;++k){
				int x=a[j+k],y=a[j+k+i];
				a[j+k]=(x+y)%mod;
				a[j+k+i]=(x+mod-y)%mod;
				if(tag==-1)a[j+k]=1ll*inv2*a[j+k]%mod,a[j+k+i]=1ll*inv2*a[j+k+i]%mod;
			}
		}
	}
}
