            for(int k=0;k<mid;k++)
            {
                int x=A[j+k],y=A[j+k+mid];
                if(type==1)  A[j+k]=(x+y)%mod,A[j+k+mid]=(x-y+mod)%mod;
                else         A[j+k]=1ll*(x+y)*inv%mod,A[j+k+mid]=1ll*(x-y+mod)*inv%mod;
            }
        }
    }
