#include <bits/stdc++.h>

using namespace std;

const int max_n = (1 << 18) + 5, mod = 998244353;

int mul(int x, int y) {
    return (1LL * x * y) % mod;
}

int power(int a, int b) {
    if (b == 0) {
        return 1 % mod;
    }
    if (b % 2 == 0) {
        return power(mul(a, a), b / 2);
    }
    return mul(a, power(a, b - 1));
}

int inv(int x) {
    return power(x, mod - 2);
}

int n, sum, a[max_n], b[max_n], ans[max_n];

void product(int *a, int *b, int *c) {
    for (int i = 0; i < (1 << n); ++i) {
        for (int j = 0; j < (1 << n); ++j) {
            c[i ^ j] += mul(a[i], b[j]);
            c[i ^ j] %= mod;
        }
    }
}

int inv2 = inv(2);

template<bool inverse>
void convolve(int n, int *a) {
    if (n == 1) {
        return;
    }
    for (int i = 0; i < n / 2; ++i) {
        int a0 = a[i], a1 = a[i + n / 2];
        a[i] = (a0 + a1) % mod;
        a[i + n / 2] = (a0 + mod - a1) % mod;
        if (inverse) {
            a[i] = mul(a[i], inv2);
            a[i + n / 2] = mul(a[i + n / 2], inv2);
        }
    }
    convolve<inverse>(n / 2, a);
    convolve<inverse>(n / 2, a + n / 2);
}

void fast_product(int n, int *a, int *b, int *c) {
    if (n == 1) {
        c[0] = mul(a[0], b[0]);
        return;
    }
    for (int i = 0; i < n / 2; ++i) {
        int a0 = a[i], a1 = a[i + n / 2];
        a[i] = (a0 + a1) % mod;
        a[i + n / 2] = (a0 + mod - a1) % mod;

        int b0 = b[i], b1 = b[i + n / 2];
        b[i] = (b0 + b1) % mod;
        b[i + n / 2] = (b0 + mod - b1) % mod;
    }
    fast_product(n / 2, a, b, c);
    fast_product(n / 2, a + n / 2, b + n / 2, c + n / 2);
    for (int i = 0; i < n / 2; ++i) {
        int c0 = c[i], c1 = c[i + n / 2];
        c[i] = mul(c0 + c1, inv2);
        c[i + n / 2] = mul(c0 + mod - c1, inv2);
    }
}

