int64 exgcd(int64 a,int64 b,int64 &c,int64 &d)
{
	if(b == 0) {
		c = 1, d = 0;
		return a;
	}
	int64 k = exgcd(b, a % b, d, c);
	d -= c * (a / b);
	return k;
}
int64 finv(int64 v)
{
	int64 c,d;
	exgcd(v,mod,c,d);
	if(c < 0) c += mod;
	return c;
