int pls(int x,int y){return x+y>=mod?x+y-mod:x+y;}
int mns(int x,int y){return x-y<0?x-y+mod:x-y;}


int n,p[M],bin[20];ll s,x,y;ll inv2=ksm(2,mod-2);
void fwt(int *a){
	for(int i=1;i<bin[n];i<<=1)
		for(int j=0;j<bin[n];j+=2*i)
			for(int k=0;k<i;k++) {
				x=a[j+k];y=a[j+k+i];a[j+k]=pls(x,y);a[j+k+i]=mns(x,y);
			}
}
void ifwt(int *a){
	for(int i=1;i<bin[n];i<<=1)
		for(int j=0;j<bin[n];j+=2*i)
			for(int k=0;k<i;k++) {
				x=a[j+k];y=a[j+k+i];a[j+k]=pls(x,y)*inv2%mod;a[j+k+i]=mns(x,y)*inv2%mod;
			}
}
