LL getmi(LL a,LL x)
{
    LL rt=1;
    while(x)
    {
        if(x&1) rt=rt*a%mod;
        a=a*a%mod,x>>=1;
    }
    return rt;
}

void FWT(LL a[],int n)
{
    for(int i=1; i<n; i<<=1)
        for(int j=0; j<n; j+=i<<1)
            rep(k,0,i-1)
            {
                LL x=a[j+k],y=a[i+j+k];
                a[j+k]=(x+y)%mod,a[i+j+k]=(x-y)%mod;
            }
}

void IFWT(LL a[],int n)
{
    for(int i=1; i<n; i<<=1)
        for(int j=0; j<n; j+=i<<1)
            rep(k,0,i-1)
            {
                LL x=a[j+k],y=a[i+j+k];
                a[j+k]=inv2*(x+y)%mod;
                a[i+j+k]=inv2*(x-y)%mod;
            }
}

int main()
{
	n=getint();
	rep(i,0,(1<<n)-1) tot+=(p[i]=getint());
	tot=getmi(tot,mod-2);
	rep(i,0,(1<<n)-1) p[i]=p[i]*tot%mod;
	FWT(p,1<<n);

	rep(i,0,(1<<n)-1) L[i]=R[i]=1-p[i];
	rep(i,1,(1<<n)-1) L[i]=L[i]*L[i-1]%mod;
	repd(i,(1<<n)-2,0) R[i]=R[i]*R[i+1]%mod;

	rep(i,0,(1<<n)-1)
	{
		LL x=1;
		if(i>0) x=L[i-1],_L[i]=_L[i-1]*(1-p[i])%mod;
		_L[i]=(_L[i]+x*(-p[i]))%mod;
	}

	repd(i,(1<<n)-1,0)
	{
		LL x=1;
		if(i<(1<<n)-1) x=R[i+1],_R[i]=_R[i+1]*(1-p[i])%mod;
		_R[i]=(_R[i]+x*(-p[i]))%mod;
	}

	rep(i,0,(1<<n)-1)
	{
		LL l=i>0?L[i-1]:1;
		LL _l=i>0?_L[i-1]:0;
		LL r=i<(1<<n)-1?R[i+1]:1;
		LL _r=i<(1<<n)-1?_R[i+1]:0;
		f[i]=l*r%mod,_f[i]=(l*_r+_l*r)%mod;
	}

	IFWT(f,1<<n),IFWT(_f,1<<n);
	rep(i,0,(1<<n)-1)
	{
		LL x=(_f[i]*f[0]-f[i]*_f[0])%mod;
		LL y=getmi(f[0]*f[0]%mod,mod-2);
		ans[i]=(x*y%mod+mod)%mod;
		printf("%lld\n",ans[i]);
	}
	return 0;
}

